        error(function(data, status, headers, config) {
            $.unblockUI();
        });
    }

    $scope.getUserTrackingDetails = function(trackID) {
        var userPhn = userInfo.UserPhn;
        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackMe";
        var proxyMethod = "POST";

        var data = {
            "userPhn": userPhn,
            "Trackingid": trackID
        };
        var proxyData = data;
        $http({
            method: proxyMethod,
            url: url,
            data: proxyData
        }).
        success(function(data, status, headers, config) {
                try{var lat = data[data.length - 1].Lat;
                var lng = data[data.length - 1].Long;
                var latlng = new google.maps.LatLng(lat, lng);
                var myOptions = {
                    zoom: 14,
                    center: latlng,
                    mapTypeId: google.maps.MapTypeId.ROADMAP
                };
                map = new google.maps.Map(mapDisplay, myOptions);
                map.addMarker($scope.createMarker(data[data.length - 1].User+ ' current location', latlng));
            }catch(e){}
                }).error(function(data, status, headers, config) {
                $.unblockUI();
            });
        }

        $scope.createMarker = function(text, latlng) {
            var marker = new google.maps.Marker({
                position: latlng,
                map: map
            });
            infowindow = new google.maps.InfoWindow({
                content: text
            });
            infowindow.open(map, marker);
