function pageprocessorcannoneerAthleticsDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var type = $routeParams.type;

  $scope.type = type;

  //$rootScope.appDisplayName = $rootScope.selSports;

  if (type == "news") {
    $scope.feed = $rootScope.newsfeeds[$routeParams.index];
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent").append(data1);

  } else if (type == "sch") {
    $scope.feed = $rootScope.schfeeds.channel.item[$routeParams.index];
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent").append(data1);
  } else if (type == "sco") {
    $scope.feed = $rootScope.scofeeds[$routeParams.index];
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent").append(data1);

  }
  $scope.openurl = function() {
    window.open($scope.feed.link, "_blank", "location=no");
  };
  $scope.openFeed = function(index) {
    window.history.back();
  };
  $scope.trusthtml = function(data) {
    return $sce.trustAsHtml(data);
  };
  $scope.convertToDate = function(stringDate) {

    var dateOut = new Date(stringDate);

    dateOut.setDate(dateOut.getDate());

    var hr = dateOut.getUTCHours();
    var mint = dateOut.getUTCMinutes();
    if (hr == 0 && mint == 0) {

      var dateString = dateOut.toString().substring(0, 16);

      var dateTimeString = dateString + "  TBA";
      return dateTimeString;

    } else {
      return dateOut;
    }
  };
  $scope.showButtonNext = true;
  $scope.showButtonPrev = true;
  $scope.isDisabled = false;
  $scope.index = $routeParams.index;
  $scope.next = function() {
    var len = $rootScope.newsfeeds.length;
    try {
      console.log($scope.index);
      if ($scope.index < len - 1) {
        $scope.feed = $rootScope.newsfeeds[++$scope.index];
        $scope.showButtonPrev = true;
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More News Feeds',
            null,
